import React, { Component } from 'react'
import ReactDOM from 'react-dom'
import { Card, CardBody, CardHeader, Table } from 'reactstrap'
import ModulesInfo from '../../../components/Card/ModulesInfo'
import ModulesTable from '../../../components/Table/ModulesTable'

class Modules extends Component {

  constructor(props) {
    super(props)
    this.state = {
      modules: [
        {
          moduleID: '',
          naam: '',
          locatie: ''
        }, 
        {
          moduleID: '',
          naam: '',
          locatie: ''
        }
        ]
    }
  }

  componentDidMount() {
    this.getModules()
  }

  getModules = _ => {
    fetch('http://localhost:4000/modules')
      .then(response =>response.json())
      .then(response => this.setState({...this.state, modules: response.data }))
      .catch(err => console.log(err))

    ReactDOM.render(document.getElementById('module-info'));
  }

  // renderModule = ({ moduleID, naam, locatie }) => (
  //   <tr>
  //     <td>{moduleID}</td>
  //     <td>{naam}</td>
  //     <td>{locatie}</td>
  //   </tr>)

  render() {
    let { modules } = this.state
    return (
      <div className="animated fadeIn">
        <Card>
          <CardHeader>
            <i className="fa fa-align-justify"></i>Modules
          </CardHeader>
          <CardBody>
            <div className="module-card">
              <ModulesInfo modules={modules}/>
              <ModulesTable />
            </div>
          </CardBody>
        </Card>
      </div>
    );
  }
}

export default Modules;
