import React, { Component } from 'react';
import { Table } from 'reactstrap'

class DeelnemerRow extends Component {

    constructor(props) {
        super(props)
    }

    componentDidMount() {
        this.getModules()
        this.getSelectedModule()
    }

    getModules = _ => {
        fetch('http://localhost:4000/modules')
            .then(response => response.json())
            .then(response => this.setState({ modules: response.data }))
            .catch(err => console.log(err))
    }

    getSelectedModule = _ => {this.setState({...this.state, currentlySelected: 1})}

    renderModule = ({ moduleID, naam, locatie }) => <tr onClick={getSelectedModule()}><td>{moduleID}</td><td>{naam}</td><td>{locatie}</td></tr>

    render() {
        let { modules } = this.props
        return (
            <div className="modules">
                <Table responsive striped hover bordered id="table-modules">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Naam</th>
                            <th>Locatie</th>
                        </tr>
                    </thead>
                    <tbody>
                        {modules.map(this.renderModule)}
                    </tbody>
                </Table>
            </div>
        )
    }
}

export default DeelnemerRow
