import React, { Component } from 'react';
import { Table } from 'reactstrap'

class DeelnemerRow extends Component {

    constructor(props) {
        super(props)
    }

    componentDidMount() {
        this.getModules()
    }

    getModules = _ => {
        fetch('http://localhost:4000/modules')
            .then(response => response.json())
            .then(response => this.setState({ modules: response.data }))
            .catch(err => console.log(err))
    }

    getRandomNumber(){
        let { usersArray } = this.state
        const min = 1
        const max = usersArray.length

        const rand = Math.round((min + Math.random() * (max - min)) - 1)
        console.log("rand:", rand)
        this.setState({...this.state, randomNumber: rand})
    }

    renderModule = ({ moduleID, naam, locatie }) => <tr><td>{moduleID}</td><td>{naam}</td><td>{locatie}</td></tr>

    render() {
        const { modules } = this.props
        return (
            <div className="modules">
                <Table responsive striped hover bordered id="table-modules">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Naam</th>
                            <th>Locatie</th>
                        </tr>
                    </thead>
                    <tbody>
                        {modules.map(this.renderModule)}
                    </tbody>
                </Table>
            </div>
        )
    }
}

export default DeelnemerRow
