import React, { Component } from 'react'
import ReactDOM from 'react-dom'
import { Card, CardBody, CardHeader, Table } from 'reactstrap'
import ModulesInfo from '../../../components/Card/ModulesInfo'
import ModulesTable from '../../../components/Table/ModulesTable'

class Modules extends Component {

  constructor(props) {
    super(props)
    this.state = {
      modules: [{
        naam: '',
        locatie: '',
        moduleID: ''
      }],
      currentlySelected: 0
    }
  }

  componentDidMount() {
    this.getModules()
  }

  getModules = _ => {
    fetch('http://localhost:4000/modules')
      .then(response =>response.json())
      .then(response => this.setState({...this.state, modules: response.data}))
      .catch(err => console.log(err))
    this.forceUpdate()
  }

  getSelectedModule = (moduleID) => {
    this.setState({ currentlySelected: moduleID - 1 })
  }

  renderModule = ({ moduleID, naam, locatie }) => <tr onClick={() => this.getSelectedModule(moduleID)}><td>{moduleID}</td><td>{naam}</td><td>{locatie}</td></tr>

  // renderModule = ({ moduleID, naam, locatie }) => (
  //   <tr>
  //     <td>{moduleID}</td>
  //     <td>{naam}</td>
  //     <td>{locatie}</td>
  //   </tr>)

  render() {
    let { modules, currentlySelected } = this.state
    const modulesObject = modules[currentlySelected]
    return (
      <div className="animated fadeIn">
        <Card>
          <CardHeader>
            <i className="fa fa-align-justify"></i>Modules
          </CardHeader>
          <CardBody>
            <div className="module-card">
              <ModulesInfo modulesObject={modulesObject}/>
              <div className="modules">
                <Table responsive striped hover bordered id="table-modules">
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Naam</th>
                      <th>Locatie</th>
                    </tr>
                  </thead>
                  <tbody>
                    {modules.map(this.renderModule)}
                  </tbody>
                </Table>
              </div>
            </div>
          </CardBody>
        </Card>
      </div>
    );
  }
}

export default Modules;
