import React, { Component } from 'react'
import { Button, Card, CardHeader, CardBody, Label, Row, Col } from 'reactstrap'
import { Bar } from 'react-chartjs-2'
import { CustomTooltips } from '@coreui/coreui-plugin-chartjs-custom-tooltips'

function Progression(props) {
    const results = props.results

    for (let i = 0; i < results.length - 1; i++) {
        if (results[i].Module_has_DeelnemerID === results[i + 1].Module_has_DeelnemerID && results[i].vragenlijstmoment === 0 && results[i + 1].vragenlijstmoment === 1) {
            let answers0 = results[i]
            let answers1 = results[i + 1]


        }
    }

    return (
        <Card>
            <CardHeader>
                Individuele vooruitgang(%) per vraag
            </CardHeader>
            <CardBody>
                <ProgressionBar/>
            </CardBody>
        </Card>
    )
}

function getPercentage(first, second) {
    const difference = second - first
    switch (difference) {
        case -4:
            return -100
        case -3:
            return -50
        case -2:
            return -25
        case -1:
            return 0
        case 0:
            return 4
        case 1:
            return 5
        case 2:
            return 6
        case 3:
            return 7
        case 4:
            return 8
        default:
            return 0
    }
}

function ProgressionBar(props) {
    // const progression = props.progression || []
    const progression = [
        {
            percentage: 25,
            vraag: 1
        },
        {
            percentage: -25,
            vraag: 2
        },
        {
            percentage: 50,
            vraag: 3
        },
        {
            percentage: 50,
            vraag: 4
        },
        {
            percentage: -50,
            vraag: 5
        },
    ]

    const options = {
        maintainAspectRatio: false,
        tooltips: {
            enabled: false,
            custom: CustomTooltips
        }
    }

    const bar = {
        labels: progression.map(i => i.vraag),
        datasets: [
            {
                label: 'Vooruitgang(%)',
                backgroundColor: '#00aee8',
                borderColor: '#00aee8',
                hoverBackgroundColor: '#00aee8',
                hoverBorderColor: '#00aee8',
                data: progression.map(i => i.percentage),
            },
        ],
    }

    return (
        <div className="chart-wrapper progression-chart">
            <Bar data={bar} options={options} height={300} />
        </div>
    )
}

class IndividualStatistics extends Component {
  render() {
      let { results } = this.props
    return (
      <div>
        <Progression results={results}/>
      </div>
    )
  }
}

export default IndividualStatistics
