import React, { Component } from 'react'
import { Button, Card, CardHeader, CardBody, Label, Row, Col } from 'reactstrap'
import { Bar, Pie } from 'react-chartjs-2'
import { CustomTooltips } from '@coreui/coreui-plugin-chartjs-custom-tooltips'

const options = {
    maintainAspectRatio: false,
    tooltips: {
        enabled: false,
        custom: CustomTooltips
    }
}

function Welcome(props) {
    var naam = props.naam + 'kaas'
    return <h1>Hello, {naam}</h1>;
}

function LocationsChart(props) {
    const locations = props.selectedLocations
    const pie = {
        labels: locations.map(i => i.label),
        datasets: [{
            data: locations.map(i => i.count),
            backgroundColor: [
                '#FF6384',
                '#36A2EB',
                '#FFCE56',
                '#7CFC00',
                '#FF0000',
                '#FFFF00'
            ],
            hoverBackgroundColor: [
                '#FF6384',
                '#36A2EB',
                '#FFCE56',
                '#7CFC00',
                '#FF0000',
                '#FFFF00'
            ],
        }]
    }
    return (
        <Card>
            <CardHeader>
                Aantal deelnemers per locatie
            </CardHeader>
            <CardBody>
                <div className="chart-wrapper">
                    <Pie data={pie} />
                </div>
            </CardBody>
        </Card>
    )
}

function getPercentage(first, second) {
    const difference = second - first
    switch (difference) {
        case 0:
            return 0
        case 1:
            return 25
        case 2:
            return 50
        case 3:
            return 75
        case 4:
            return 100
        default:
            return 0
    }
}

function Progression(props) {

    const results = props.results
    let averageProgression1 = [
        { percentage: '-100%', count: 0 },
        { percentage: '-75%', count: 2 },
        { percentage: '-50%', count: 2 },
        { percentage: '-25%', count: 2 },
        { percentage: '0%', count: 2 },
        { percentage: '25%', count: 4 },
        { percentage: '50%', count: 5 },
        { percentage: '75%', count: 2 },
        { percentage: '100%', count: 1 }
    ]
    let averageProgression2, averageProgression3, averageProgression4, averageProgression5, averageProgression6, averageProgression7, averageProgression8, averageProgression9 = averageProgression1

    for (let i = 0; i < results.length - 1; i++) {
        if (results[i].Module_has_DeelnemerID === results[i + 1].Module_has_DeelnemerID && results[i].vragenlijstmoment === 0 && results[i + 1].vragenlijstmoment === 1) {
            console.log('Match')
            console.log('Pair: ' + results[i].Module_has_DeelnemerID)
            let answers0 = results[i]
            let answers1 = results[i + 1]
            averageProgression1.push(getPercentage(answers0.vraag1, answers1))
        } else {
            console.log('No match')
        }
    }
    console.log('Average Progression: ' + averageProgression)

    const bar = {
        labels: averageProgression.map(i => i.percentage),
        datasets: [
            {
                label: 'Aantal Deelnemers',
                backgroundColor: 'rgb(0,0,255)',
                borderColor: 'rgb(0,0,0)',
                borderWidth: 1,
                height: '100px',
                hoverBackgroundColor: 'rgba(255,99,132,0.4)',
                hoverBorderColor: 'rgba(255,99,132,1)',
                data: averageProgression.map(i => i.count),
            },
        ],
    };

    return (
        <Card>
            <CardHeader>
                Vooruitgang(%) per vraag
            </CardHeader>
            <CardBody>
                <div className="chart-wrapper progression-chart">
                    <Bar data={bar} options={options} height={150}/>
                </div>
            </CardBody>
        </Card>
    )
}

class GroupStatistics extends Component {
    render() {
        let { results, selectedLocations } = this.props
        return (
            <div className="group-statistics">
                <Row>
                    <Col sm="6">
                        <LocationsChart selectedLocations={selectedLocations} />
                    </Col>
                    <Col sm="6">
                        <LocationsChart selectedLocations={selectedLocations} />
                    </Col>
                </Row>
                <Progression results={results} />
            </div>
        )
    }
}

export default GroupStatistics
