import React, { Component } from 'react'
import { Badge, Button, Card, CardBody, CardFooter, CardHeader, Col, Collapse, Fade, Row } from 'reactstrap'
import {
    Accordion,
    AccordionItem,
    AccordionItemTitle,
    AccordionItemBody,
} from 'react-accessible-accordion'
import _ from 'lodash'

class LocationFilter extends Component {

    constructor(props) {
        super(props)
        this.state = {
            collapse: false,
            accordion: [true, false, false],
            custom: [false],
            status: 'Closed',
            fadeIn: true,
            timeout: 300,
            locations: [{ locatie: 'Rotterdam' }, { locatie: 'Utrecht' }, { locatie: 'Amsterdam' }]
        };

        this.toggleCustom = this.toggleCustom.bind(this);
    }

    componentDidMount() {
        this.testLodash()
    }

    toggleCustom(tab) {
        const prevState = this.state.custom;
        const state = prevState.map((x, index) => tab === index ? !x : false);

        this.setState({
            custom: state,
        });
    }

    testLodash() {
        var locations = _.chain(this.props.modules).countBy('locatie').map((count, locatie) => ({ locatie })).value()
        console.log(locations)
        this.setState({
            locations: state,
        });
    }

    renderFilterOptions = ({locatie}) =><div>{locatie}</div>

    render() {
        return (
            <div id="locationFilter" data-children=".item">
                <div className="item">
                    <Button onClick={() => this.toggleCustom(0)} aria-expanded={this.state.custom[0]} aria-controls="LocationAccordion">
                        Locatie
                    </Button>
                    <Collapse isOpen={this.state.custom[0]} data-parent="#exampleAccordion">
                        {this.state.locations.map(this.renderFilterOptions)}
                    </Collapse>
                </div>
            </div>
        )
    }
}

export default LocationFilter
