import React, { Component } from 'react'
import { Button, Collapse } from 'reactstrap'
import _ from 'lodash'

class LocationFilter extends Component {

    constructor(props) {
        super(props)
        this.state = {
            collapse: false,
            accordion: [true, false, false],
            custom: [false],
            status: 'Closed',
            fadeIn: true,
            timeout: 300
        };

        this.toggleCustom = this.toggleCustom.bind(this);
    }

    toggleCustom(tab) {
        const prevState = this.state.custom;
        const state = prevState.map((x, index) => tab === index ? !x : false);

        this.setState({
            custom: state,
        });
    }

    renderFilterOptions = ({ locatie }) => (
        <li className="filter-options">
            <label id={locatie}>
                <input type="checkbox" id={locatie + '-filter'} />{locatie}
            </label>
        </li>
    )

    myFunction() {
        console.log('hello')
        var input, filter, ul, li, a, i, txtValue;
        input = document.getElementById("myInput");
        filter = input.value.toUpperCase();
        ul = document.getElementById("location-list");
        li = ul.getElementsByTagName("li");
        for (i = 0; i < li.length; i++) {
            a = li[i].getElementsByTagName("label")[0];
            txtValue = a.textContent || a.innerText;
            if (txtValue.toUpperCase().indexOf(filter) > -1) {
                li[i].style.display = "";
            } else {
                li[i].style.display = "none";
            }
        }
    }

    render() {
        return (
            <div className="locationFilter filter" data-children=".item">
                <div className="item">
                    <Button onClick={() => this.toggleCustom(0)} aria-expanded={this.state.custom[0]} aria-controls="LocationAccordion">
                        Locatie
                        <i className="arrow"></i>
                    </Button>
                    <Collapse isOpen={this.state.custom[0]} data-parent="#exampleAccordion">
                        <input type="text" id="myInput" onkeyup={this.myFunction()} placeholder="Search for names.." title="Type in a name" />
                        <div class="multiselect">
                            <div class="selectBox" onclick="showCheckboxes()">
                                <div id="checkboxes" class="margin">
                                    <ul id="location-list">
                                        {this.props.locations.map(this.renderFilterOptions)}
                                    </ul>
                                </div>
                            </div>
                        </div>
                    </Collapse>
                </div>
            </div>
        )
    }
}

export default LocationFilter
