import React, { Component } from 'react'
import Slider, { Range } from 'rc-slider'
import { Row, Col } from 'reactstrap'

class AgeSlider extends Component {
    constructor(props) {
        super(props)
        this.state = {
            min: 0,
            max: 100,
        }
    }

    onSliderChange = (value) => {
        console.log(value)
    }

    onMinChange = (e) => {
        this.setState({
            min: +e.target.value || 0,
        }, () => console.log('MIN'))
    }

    onMaxChange = (e) => {
        this.setState({
            max: +e.target.value || 100,
        }, () => console.log('MAX'))
    }

    render() {
        return (
            <div className="age-slider">
                <Row>
                    <Col xs="4" className="slider-min">
                        <label>Minimale Leeftijd: </label>
                        <input type="number" value={this.state.min} onChange={this.onMinChange} />
                    </Col>
                    <Col xs="4"></Col>
                    <Col xs="4" className="slider-max">
                        <label>Maximale Leeftijd: </label>
                        <input type="number" value={this.state.max} onChange={this.onMaxChange} />
                    </Col>
                </Row>
                <Range defaultValue={[0, 50]} min={this.state.min} max={this.state.max}
                    onChange={this.onSliderChange}
                />
            </div>
        )
    }
}

export default AgeSlider
