import React, { Component } from 'react'
import DeelnemerInfo from '../../../components/Card/DeelnemerInfo'
import DeelnemerTable from '../../../components/Table/DeelnemerTable'

class Deelnemer extends Component {

    constructor(props) {
        super(props)
        this.state = {
            deelnemers: [{
                deelnemerID: '',
                voornaam: '',
                achternaam: '',
                geboortedatum: '',
                verblijfstatus: '',
                moduleNaam: ''
            }],
            currentlySelectedDeelnemer: 0
        }
        this.getSelectedDeelnemer = this.getSelectedDeelnemer
    }

    componentDidMount() {
        this.getDeelnemers()
    }

    getSelectedDeelnemer(data) {
        this.setState({ currentlySelectedDeelnemer: data - 1 })
    }

    getDeelnemers = _ => {
        fetch(`http://localhost:4000/moduledeelnemers?modules=${this.props.match.params.id}`)
            .then(response => response.json())
            .then(response => this.setState({ deelnemers: response.data }))
            .catch(err => console.log(err))
    }

    calculateAge(geboortedatum) { // birthday is a date
        // console.log(geboortedatum)
        // var ageDifMs = Date.now() - geboortedatum.getTime();
        // var ageDate = new Date(ageDifMs); // miliseconds from epoch
        // return Math.abs(ageDate.getUTCFullYear() - 1970);
        var geboortedatum = geboortedatum
        var today = new Date();
        var birthDate = new Date(geboortedatum);
        var age = today.getFullYear() - birthDate.getFullYear();
        var m = today.getMonth() - birthDate.getMonth();
        if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
            age = age - 1;
        }
        return age
    }

    renderDeelnemer = ({ deelnemerID, voornaam, achternaam, geboortedatum }) => (
        <tr key={deelnemerID} onClick={() => this.getSelectedDeelnemer(deelnemerID)}>
            <td>{deelnemerID}</td>
            <td>{voornaam + ' ' + achternaam}</td>
            <td>{this.calculateAge(geboortedatum)}</td>
        </tr>)

    render() {
        const { deelnemers, currentlySelectedDeelnemer } = this.state
        const deelnemersObject = deelnemers[currentlySelectedDeelnemer]
        return (
            <div className="animated fadeIn">
                <div className="card">
                    <div className="card-header">
                        <i className="icon-people"></i>Deelnemers - Module {deelnemers[0].naam}
                    </div>
                    <div className="card-body">
                        <div className="deelnemer-card">
                            <DeelnemerInfo deelnemersObject={deelnemersObject}/>
                            <DeelnemerTable />
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Deelnemer
