import React, { Component } from 'react'
import { Button, Card, CardHeader, CardBody, Form } from 'reactstrap'
import Select from 'react-select';

class RapportageV2 extends Component {

    constructor(props) {
        super(props)
        this.state = {
            deelnemers: [{
                deelnemerID: '',
                voornaam: '',
                achternaam: '',
                geboortedatum: '',
                verblijfstatus: ''
            }],
            modules: [{
                naam: '',
                locatie: '',
                moduleID: '',
                jaar: '',
                voornaam: '',
                achternaam: '',
                telefoonnummer: '',
                email: '',
                amountOfDeelnemers: ''
            }],
            locations: [],
            statuses: [],
            everything: []
        }
    }

    state = {
        selectedOption: null,
    }
    handleChange = (selectedOption) => {
        this.setState({ selectedOption });
        console.log(`Option selected:`, selectedOption);
    }

    getAll = _ => {
        fetch('http://dev.jasperzwiers.eu/moduledeelnemers/all')
            .then(response => response.json())
            .then(response => this.setState({ ...this.state, everything: response.data }, () => { this.addFilter() }))
            .catch(err => console.log(err))
        this.forceUpdate()
    }

    render() {
        return (
            <div className="animated fadeIn">
                <div className="rapportagev2">
                    <Card>
                        <CardHeader>
                            Rapportage Selectie
